CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_checadas_semanales`(IN `v_id_empleado` INT, IN `v_fecha_inicial` DATE, IN `v_fecha_final` DATE)
BEGIN
set @nombre  = (select nombre from empleado where id_empleado = v_id_empleado);
select @nombre as nombre, c.fecha, c.dia, ifnull(time(ee.fecha_modificacion), 'Sin Registro') as hora_entrada, ee.estatus as estatus_entrada, ee.observaciones as obs_entrada, ifnull(ee.foto, 'default.jpg') as foto_entrada, ee.tipo as tipo_entrada, ifnull(time(ss.fecha_modificacion), 'Sin Registro') as hora_salida, ss.estatus as estatus_salida, ss.observaciones as obs_salida, ifnull(ss.foto, 'default.jpg') as foto_salida, ss.tipo as tipo_salida from calendario c
left join (select ce.fecha_modificacion, ce.foto, ce.estatus, ce.id_empleado, ce.tipo, date(ce.fecha_modificacion) as fecha, le.observaciones from checar ce left join tbl_log le on ce.id_entrada = le.IdChecada where ce.tipo = 'E' and ce.id_empleado = v_id_empleado and ce.activo = 'S' ) ee on c.fecha = ee.fecha
left join (select cs.fecha_modificacion, cs.foto, cs.estatus, cs.id_empleado, cs.tipo, date(cs.fecha_modificacion) as fecha, ls.observaciones from checar cs left join tbl_log ls on cs.id_entrada = ls.IdChecada where cs.tipo = 'S' and cs.id_empleado = v_id_empleado and cs.activo = 'S') ss on c.fecha = ss.fecha
where c.fecha between v_fecha_inicial and v_fecha_final and c.activo = 'S'
order by fecha;
END ;;
