  SELECT `hora_salida` INTO t_horaSalida FROM `empleado` WHERE `id_empleado` = v_id_empleado;
  IF EXISTS(SELECT * FROM `checadores` WHERE `ip` = v_ip AND activo = 'S') THEN
      BEGIN
         IF EXISTS(SELECT * FROM `empleado` WHERE `id_empleado` = v_id_empleado AND activo = 'S') THEN
            BEGIN
             IF((SELECT password FROM `empleado` WHERE `id_empleado` = v_id_empleado AND activo = 'S') IS NOT NULL) THEN
                  BEGIN
                          IF EXISTS(SELECT * FROM `empleado` WHERE `id_empleado` = v_id_empleado AND `password` = v_password AND activo = 'S') THEN
