CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_modificar_checada`(IN `v_id_checada` INT, IN `v_hora_nueva` TIME, IN `v_idusuario` INT, IN `v_observaciones` VARCHAR(100))
    NO SQL
BEGIN
    DECLARE t_id_empleado INT;
    DECLARE diff_checada INT;
    SELECT `id_empleado` INTO t_id_empleado FROM `checar` WHERE `id_entrada` = v_id_checada;
    IF EXISTS(SELECT * FROM checar WHERE id_entrada = v_id_checada AND activo = 'S') THEN
      BEGIN
        IF ((SELECT `tipo` FROM `checar` WHERE `id_entrada` = v_id_checada) = 'E') THEN
          BEGIN
            DECLARE t_horaEntrada time;
            SELECT `hora_entrada` INTO t_horaEntrada FROM `empleado` WHERE `id_empleado` = t_id_empleado;
            SELECT (TIMESTAMPDIFF(SECOND, t_horaEntrada, TIME(v_hora_nueva)) / 60) INTO diff_checada;
            UPDATE checar SET  fecha_modificacion = CONCAT_WS(' ',DATE(fecha_modificacion), v_hora_nueva), estatus = IF(diff_checada > 16, IF(diff_checada > 30, 'F', 'R'), 'N'), diff = diff_checada WHERE id_entrada = v_id_checada;
            SELECT 'TRUE' AS STATUS;
