CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_total_faltas_checadas`(IN `v_fecha_inicial` DATE, IN `v_fecha_fecha_final` DATE)
BEGIN
SELECT  vista_todas_checadas.id_empleado, vista_todas_checadas.nombre,
    COUNT(
        CASE
            WHEN vista_todas_checadas.tipo = 'E' AND  vista_todas_checadas.estatus = 'N'
            THEN 1
            ELSE NULL
        END
    ) AS 'entrada_normal',
    COUNT(
        CASE
            WHEN vista_todas_checadas.tipo = 'E' AND  vista_todas_checadas.estatus = 'R'
            THEN 1
            ELSE NULL
        END
    ) AS 'entrada_retardo',
    COUNT(
        CASE
            WHEN vista_todas_checadas.tipo = 'E' AND  vista_todas_checadas.estatus = 'F'
            THEN 1
            ELSE NULL
        END
    ) AS 'entrada_falta',
    COUNT(
        CASE
            WHEN vista_todas_checadas.tipo = 'S' AND  vista_todas_checadas.estatus = 'N'
            THEN 1
            ELSE NULL
        END
    ) AS 'salida_normal',
    COUNT(
        CASE
            WHEN vista_todas_checadas.tipo = 'S' AND  vista_todas_checadas.estatus = 'A'
            THEN 1
            ELSE NULL
        END
    ) AS 'salida_anticipada',
    COUNT(
        CASE
            WHEN vista_todas_checadas.tipo = 'N/A' AND  vista_todas_checadas.estatus = 'N/P'
            THEN 1
            ELSE NULL
        END
    ) AS 'no_presento'
FROM vista_todas_checadas
WHERE DATE(vista_todas_checadas.fecha) BETWEEN v_fecha_inicial AND v_fecha_fecha_final
GROUP BY vista_todas_checadas.id_empleado
ORDER BY vista_todas_checadas.nombre;
END ;;
