select c.fecha, c.dia, ifnull(time(ee.fecha_modificacion), 'Sin Registro') as hora_entrada, ee.estatus as estatus_entrada, ee.tipo as tipo_entrada, ifnull(time(ss.fecha_modificacion), 'Sin Registro') as hora_salida, ss.estatus as estatus_salida, ss.tipo as tipo_salida from calendario c
left join (select ce.fecha_modificacion, ce.foto, ce.estatus, ce.id_empleado, ce.tipo, date(ce.fecha_modificacion) as fecha from checar ce where ce.tipo = 'E'  and ce.activo = 'S') ee on c.fecha = ee.fecha
left join (select cs.fecha_modificacion, cs.foto, cs.estatus, cs.id_empleado, cs.tipo, date(cs.fecha_modificacion) as fecha from checar cs where cs.tipo = 'S'  and cs.activo = 'S') ss on c.fecha = ss.fecha
where c.fecha between v_fecha_inicial and v_fecha_final and c.activo = 'S'
order by fecha;
END ;;
