left join tbl_log le ON le.IdChecada = i.id_entrada
left join tbl_log ls ON ls.IdChecada = ss.id_salida
WHERE e.activo = 'S' and i.tipo = 'E' and date(i.fecha_modificacion) = v_fecha_impresion AND i.activo = 'S' order by i.fecha_modificacion;
